svg.script:
var tooltip = null;
function showTooltip(e, text) {
  if (!tooltip) { tooltip = document.createElement("div"); tooltip.id = "tooltip"; document.body.appendChild(tooltip); }
  tooltip.innerHTML = text.split("\\n").join("<br>");
  tooltip.style.left = (e.clientX + 10) + "px";
  tooltip.style.top = (e.clientY + 10) + "px";
  tooltip.style.display = "block";
}
function hideTooltip() { if (tooltip) tooltip.style.display = "none"; }
function highlightNode(callsign) {
  // Dim all connections
  var connections = document.querySelectorAll(".connection");
  connections.forEach(function(conn) {
    conn.classList.add("dimmed");
    conn.classList.remove("highlighted");
  });
  // Highlight connections for this node
  var nodeConns = document.querySelectorAll(".connection[data-from='" + callsign + "'], .connection[data-to='" + callsign + "']");
  nodeConns.forEach(function(conn) {
    conn.classList.remove("dimmed");
    conn.classList.add("highlighted");
  });
}
function unhighlightAll() {
  var connections = document.querySelectorAll(".connection");
  connections.forEach(function(conn) {
    conn.classList.remove("dimmed", "highlighted");
  });
}
